    private void btnCrearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCrearActionPerformed
        Persona clon = (Persona) original.clonar();
        clon.setNombre(inputNombre.getText());
        clon.setApellidos(inputApellidos.getText());
        clon.setCorreoElectronico(inputCorreoElectronico.getText());
        clon.setNivelDeEstudios(inputNivelEstudios.getText());
        padre.addSerVivo(clon);
        dispose();
    }//GEN-LAST:event_btnCrearActionPerformed

