    private void btnCrearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCrearActionPerformed
        Creador creador = new CreadorSeresVivos();
        Persona persona = (Persona) creador.crearServivo(Creador.TipoSerVivo.Persona);
        persona.setNombre(inputNombre.getText());
        persona.setApellidos(inputApellidos.getText());
        persona.setTelefono(inputTelefono.getText());
        persona.setDireccion(inputDireccion.getText());
        persona.setCodigoPostal(inputCP.getText());
        persona.setCiudad(inputCiudad.getText());
        persona.setNivelEconomico(inputNivelEconomico.getText());
        persona.setCorreoElectronico(inputCorreoElectronico.getText());
        persona.setNivelDeEstudios(inputNivelEstudios.getText());
        padre.addSerVivo(persona);
        dispose();
    }//GEN-LAST:event_btnCrearActionPerformed

